
var f= function(y){
    var ans = new Array(y);  
    for(let i = 0; i<ans.length;i++)
    ans[i] = new Array(y);
    var left = 0 ; right = y-1,top = 0,down = y-1;
    var tmp = 1;
    var dir = 0;
    while(left <= right && top <= down){
        if(dir === 0){           
            for(let i = left ;i <=right; i++){
                ans[top][i] = tmp;
                tmp++;
            }
            dir=1;
            top++;       
    }
    else  if(dir === 1){
        for(let i = top ;i <=down; i++){
            ans[i][right] = tmp;
            tmp++;
        }
        dir=2;
        right--;  
}
else if(dir === 2){         
    for(let i = right ;i >= left; i--){
        ans[down][i] = tmp;
        tmp++;
    }
    dir=3;
    down--;
}
 else if(dir === 3){          
    for(let i = down ;i >=top; i--){
        ans[i][left] = tmp;
        tmp++;
    }
    dir=0;
    left++;
}
}
return ans;
}

module.exports = f;


